import { createSlice } from "@reduxjs/toolkit";

const photos = createSlice({
  name: "photos",
  initialState: [],
  reducers: {
    addPhoto: (state, action) => {
      const newPhoto = action.payload;
      state.push(newPhoto);
      return state
    },
  },
})
const { reducer, actions } = photos;
export const { addPhoto } = actions;
export default reducer;
