import { useForm } from "react-hook-form";
import { Button, Form, FormGroup, Input, Label } from "reactstrap";
import { PHOTO_CATEGORY_OPTIONS } from "../../../../constants/global";
import Images from "../../../../constants/images";

function PhotoForm(props) {
  const {
    register,
    handleSubmit,
    watch,
    formState:{errors}
  }= useForm();
  const onLoginSubmit=(data)=>{
  console.log(data.value);  
  }
  return (
    <Form onSubmit={handleSubmit(onLoginSubmit)}>
      <div >
        <Label for="titleId">Title</Label>
        <Input ref={register} name="title" id="titleId" placeholder="Eg: Wow nature ... " />
      </div>

      <div>
        <Label for="categoryId">Category</Label>
        <Input
          id="categoryId"
          name="categoryId"
          type="select"
          ref={register}

        >
            {PHOTO_CATEGORY_OPTIONS.map(item=>
                <option key={item.id}>{item.label}</option>
            )}
        </Input>
      </div>
      <div>
          <Label for="categoryId">Photo</Label>
          <div>
              <Button ref={register} type="button" >Random a photo</Button>
          </div>
          <div>

          <img width="200px" height="200px" src={Images.SUNSET_BG} />
          </div>
      </div>
      <div>
          <Button onSubmit={handleSubmit(onLoginSubmit)} color="primary">Add to album</Button>
      </div>
    </Form>
  );
}
export default PhotoForm;
