import { useState } from "react";
import { Controller, useForm } from "react-hook-form";
import { Button, Form, FormGroup, input, Label } from "reactstrap";
import RandomPhoto from "../../../../components/RandomPhoto";
import { PHOTO_CATEGORY_OPTIONS } from "../../../../constants/global";
import Images from "../../../../constants/images";


function PhotoForm() {
  const {
    register,
    handleSubmit,
  } = useForm();
  const onSubmit = (data) => {
    console.log(data);
  };
  const [imageUrl, setImageUrl ] = useState("https://picsum.photos/id/237/200/300")
  const getRandomImageUrl=()=>{

    const randomId = Math.trunc(Math.random() * 2000);
    setImageUrl(`https://picsum.photos/id/${randomId}/200/300`);
    return imageUrl
  }

  return (
    <form onSubmit={handleSubmit(onSubmit)}>
      <div>
        <Label for="titleId">Title</Label>
        <input
          {...register("title")}
          // name="title"
          // id="titleId"
          placeholder="Eg: Wow nature ... "
        />
      </div>

      <div>
        <Label for="categoryId">Category</Label>
        <select id="categoryId" name="categoryId" {...register("category")}>
          {PHOTO_CATEGORY_OPTIONS.map((item) => (
            <option  key={item.id}>{item.label}</option>
          ))}
        </select>
      </div>
      <div>
        <Label for="categoryId">Photo</Label>
        </div>
        <button

          onClick={getRandomImageUrl}
        >
          Random a Photo
        </button>
        <div>
        <Controller
            control={control}
            name="ReactDatepicker"
            render={({ field }) => (
              <RandomPhoto 
          // value={imageUrl}
          imagesUrl={imageUrl}

          {...register("photo")}
          ></RandomPhoto>
            )}
        ></Controller>
          </div>
      <div>
        <Button type="submit" color="primary">
          Add to album
        </Button>
      </div>
    </form>
  );
}
export default PhotoForm;
