import { useState } from "react";
import { Button } from "reactstrap";
import "./RandomPhoto.scss";

function RandomPhoto() {
  // const { name, imageUrl, onImageUrlChange, onRandomButtonBlur } = props;
  const { imagesUrl, setImagesUrl } = useState("https://picsum.photos/id/121/200/300");
  const getRandomImageUrl = () => {
    const randomId = Math.trunc(Math.random() * 2000);

    setImagesUrl(`https://picsum.photos/id/${randomId}/200/300`);
  };

  // const handleRandomPhotoClick = async () => {
  //   if (onImageUrlChange) {
  //     const randomImageUrl = getRandomImageUrl();
  //     onImageUrlChange(randomImageUrl);
  //   }
  // };
  return (
    <div className="random-photo">
      <div className="random-photo__button">
        <button

          // name={name}
          // onBlur={onRandomButtonBlur}
          onClick={getRandomImageUrl}
        >
          Random a Photo
        </button>
      </div>
      <div className="random-photo__photo">{imagesUrl &&(<img src={imagesUrl} alt={imagesUrl} />)}

      </div>
    </div>
  );
}
export default RandomPhoto;
